con:config.script:
import jxl.*

//I am working with excel write - first thing is the path
filepath = "C:\\Users\\PRATYASHEECHANGMAI\\SDETjar\\SdetData\\CustomerID_RestData.xls"
def accessToken = "185622b6c5309db66c32f0adbc0666b76511105b"
File excel = new File(filepath)
//excel contains 2 things - workbook and sheet
Workbook wb = Workbook.getWorkbook(excel)
//i have to get the sheet
Sheet sh = wb.getSheet("Sheet1")

def row = sh.getRows().toInteger()
def columns = sh.getColumns().toInteger()
log.info "The number of rows are: $row"
log.info "The number of coulmns are: $columns"

for(def i=1;i<row;i++) {
	Cell c = sh.getCell(0,i)
	log.info ("The id is" + c.getContents())
	def get = new URL("http://rest-api.upskills.in/api/rest_admin/customers/" + c.getContents()).openConnection()
	//openConnections is for between jar files - it helps to run the url and get its content
	get.setRequestProperty("Authorization","bearer " + accessToken)
	def getRC = get.getResponseCode();
	log.info "The response code is " + getRC

	if(getRC.equals("200")) {
		log.info (get.getInputStream().getText())
	}

}

import groovy.sql.Sql

def accessToken = "185622b6c5309db66c32f0adbc0666b76511105b"

def url = "jdbc:mysql://localhost:3306/sdetdb"
def user = "root"
def password = "root"
def driver = "com.mysql.jdbc.Driver"

//this is to register with mysql jdbc driver - if not present it wont connect
com.eviware.soapui.support.GroovyUtils.registerJdbcDriver(driver)

//connection to sql instance
def sql = Sql.newInstance(url,user,password,driver)

//run the query - each row is iteration
sql.eachRow("select * from customer")
{
	//row -> is for row increemnt
	row -> 
	def customerid = "$row.id"
	log.info customerid

	def get = new URL("http://rest-api.upskills.in/api/rest_admin/customers/" + customerid).openConnection()
	//openConnections is for between jar files - it helps to run the url and get its content
	get.setRequestProperty("Authorization","bearer " + accessToken)
	def getRC = get.getResponseCode();
	log.info "The response code is " + getRC

	if(getRC.equals(200)) {
		log.info (get.getInputStream().getText())
	}
	else {
		log.info "what's wrong"
	}
}

sql.close()

